import React, { Component } from 'react'
import PropTypes from 'prop-types';

class User extends Component {
  static defaultProps = {
    name : "No info for name",
    salary : "No info for salary",
    department : "No info for department"
  }

  render() {
      // Destructing
      const {name, salary, department} = this.props;
      return (
        <div>
          <ul>
            <li>Name: {"==>"+ name} <i class="fas fa-bullhorn"></i> </li>
            <li>department: {department}  </li>
            <li> Salary: {salary} <i class="fas fa-dollar-sign"></i> <i class="fas fa-euro-sign"></i></li>
          </ul>
        </div>
    )
  }
}


User.defaultProps = {
  name : "No info for name",
  salary : "No info for salary",
  department : "No info for department"

}

User.propTypes = {
  name : PropTypes.string.isRequired,
  salary : PropTypes.string.isRequired,
  department : PropTypes.string.isRequired
}

export default User;
